import React from 'react';
import './Cards.css';
import Card from './Card.jsx';

export default function Cards({cities, onClose/*,onFilter */}) {
  return (
    <div className='cards'>
      {cities.map(c => <Card
          id={c.id}
          key={c.id}
          max={c.max}
          min={c.min}
          name={c.name}
          img={c.img}
          onClose={() => onClose(c.id)}
          /* onFilter={()=>onFilter(c.id)} */
        /> )}
    </div>
  );
}
